import { useState, useEffect } from 'react'
import axios from 'axios'



const App = () => {

  const [newSearch, setNewSearch] = useState('')
  const [countries, setCountries] = useState([])

  useEffect(() => {
    if (newSearch) {
      axios
        .get(`https://restcountries.com/v3.1/all`)
        .then(response => {
          setCountries(response.data)
    })
  }}, [newSearch])

  const handleSearchChange = (event) => {
    setNewSearch(event.target.value)
  }

  const searchResults = newSearch
  ? countries.filter(country => country.name.official.toLowerCase().includes(newSearch.toLowerCase()))
  : countries

  console.log({searchResults}, {newSearch})

  return (
    <div>
    <form>
      <div>
        find countries 
        <input
          value={newSearch}
          onChange={handleSearchChange}
        />
      </div>
    </form>
    {(searchResults.length === 1)
      ? <p>yksvaan</p>
      : (searchResults.length < 11 && searchResults.length !== [])
        ? <dl style={{listStyle: 'none'}}>
          {searchResults.map(result => 
          <dt key={result.name.official}>{result.name.official}</dt>
          )}
        </dl>
        : <p>Too many matches, specify another filter</p>
    }
    </div>
  )
}

export default App;
