import { useState, useEffect } from 'react'

import Add from './components/Add'
import Filter from './components/Filter'
import List from './components/List'

import personService from './services/persons'

const App = () => {

  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newSearch, setNewSearch] = useState('')

  useEffect(() => {
    personService
      .getAll()
      .then(response => {
        //console.log('promise fulfilled')
        setPersons(response.data)
      })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }
    persons.some(person => person.name === newName)
    ? changeNumber(newName)
    : personService
      .create(personObject)
      .then(response => {
      setPersons(persons.concat(response.data))
    })
    setNewName('')
    setNewNumber('')
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchChange = (event) => {
    setNewSearch(event.target.value)
  }

  const searchResults = newSearch
  ? persons.filter(person => person.name.toLowerCase().includes(newSearch.toLowerCase()))
  : persons

  const deleteName = (id) => {
    personService
      .remove(id)
      setPersons(persons.filter(person => person.id !== id))
      console.log(`persons ${persons}`)
      }

  const changeNumber = newName => {
    window.confirm(`${newName} is already added to phonebook, replace the old number with a new one?`) 
    const person = persons.find(n => n.name === newName)
    const changedPerson = {...person, number: newNumber}
    personService
      .update(changedPerson.id, changedPerson)
      setPersons(persons.map(person => person.id !== changedPerson.id ? person : changedPerson))
      }


  return (
    <div>

      <Filter newSearch={newSearch} handleSearchChange={handleSearchChange}  />

      <h3>add a new</h3>
      <Add addPerson={addPerson} newName={newName} handlePersonChange={handlePersonChange} newNumber={newNumber} handleNumberChange={handleNumberChange} /> 

      <h3>Numbers</h3>
      <List searchResults={searchResults} deleteName={deleteName}/>

    </div>
  )
}

export default App
