import { useState } from 'react'

const Add = (props) => {
  return (
    <div>
      <form onSubmit={props.addPerson}>
          <div>
            name: 
            <input
              value={props.newName}
              onChange={props.handlePersonChange} />
            <br></br>number:
            <input
              value={props.newNumber}
              onChange={props.handleNumberChange} />
          </div>
          <div>
            <button type="submit">add</button>
          </div>
        </form>
    </div>
  )
}

const List = (props) => {
  return (
    <div>
      <dl style={{listStyle: 'none'}}>
      {props.searchResults.map(result => 
        <dt key={result.name}>{result.name} {result.number}</dt> 
        )}
      </dl>
    </div>
  )
}

const Filter = (props) => {
  return (
    <div>
      <form>
        <div>
          filter shown with 
          <input
            value={props.newSearch}
            onChange={props.handleSearchChange}
          />
        </div>
      </form>
    </div>
   )
 }


const App = () => {

  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])

  const [newName, setNewName] = useState('')

  const [newNumber, setNewNumber] = useState('')

  const [newSearch, setNewSearch] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }
    persons.some(person => person.name === newName)
    ? alert(`${newName} is already added to phonebook`)
    : setPersons(persons.concat(personObject))
    setNewName('')
    setNewNumber('')
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchChange = (event) => {
    setNewSearch(event.target.value)
  }

  const searchResults = newSearch
  ? persons.filter(person => person.name.toLowerCase().includes(newSearch.toLowerCase()))
  : persons

  return (
    <div>

      <h2>Phonebook</h2>
      <Filter newSearch={newSearch} handleSearchChange={handleSearchChange}  />

      <h3>add a new</h3>
      <Add addPerson={addPerson} newName={newName} handlePersonChange={handlePersonChange} newNumber={newNumber} handleNumberChange={handleNumberChange} /> 

      <h3>Numbers</h3>
      <List searchResults={searchResults} />

    </div>
  )
}

export default App
